import React from 'react';
import ReactDOM from 'react-dom';
import moment from 'moment-timezone';




const Horloge = ({city}) => {


	if (!city){
		city = "Los_Angeles"
	}

	return(
		<div> {"Heure à " + city +" " +moment().tz(city).format("DD/MM/YYYY HH:mm")}  </div>
		)

	}
export default Horloge;
