import React from 'react';
import './Horloge.css';

class Heures extends React.Component {
	render(){
		return(

			<div className="haiguille">
				<div className="hour" id ="hour">
				</div>
			</div>
		)
	}
}

class Minutes extends React.Component {
	render(){
		return(

			<div className="maiguille">
				<div className="minute" id ="minute">
				</div>
			</div>
		)
	}
}

class Secondes extends React.Component {
	render(){
		return(

			<div className="saiguille">
				<div className="seconde" id ="seconde">
				</div>
			</div>
		)
	}
}

class Horloge extends React.Component {

	constructor(props){
		super(props);
		//initalisation de l'état
		this.state = {

			date : new Date()
		};
	}

	//setInterval va exécuter la fonction clock toutes les secondes 
	//pour reproduire une vraie horloge
	componentDidMount() {
		this.heure = setInterval(()=>this.clock(),1000);
	}

	clock(){

		const deg = 6;

		let day = this.state.date;
		let heures = date.getHours() * 30;
		let minutes = date.getMinutes() * deg;
		let secondes = date.getSecondes() * deg;

		this.setState({
			date: new Date()
		});

		hour.style.transform = 'rotateZ('+(heures+(minutes/12))+'deg)';
    	minute.style.transform = 'rotateZ('+minutes+'deg)';
    	seconde.style.transform = 'rotateZ('+secondes+'deg)';
  }
  	render() {
    return (
      <div>
        <Time date={this.state.date} />
      </div>
    );
  }
}

const Time = (props) => {
	return(
		<div className="horloge">
			<h2>{this.state.date.toLocaleTimeString()}</h2>
			<Heures />
			<Minutes />
			<Secondes />
      	</div>
	)
}

export default Horloge;
