import React from 'react';
import './Horloge.css';

class Horloge extends React.Component {

	constructor(props){
		super(props);
		//initalisation de l'état
		this.state = {date : new Date()};
	}

	//setInterval va exécuter la fonction clock toutes les secondes 
	//pour reproduire une vraie horloge
	componentDidMount() {
		this.heure = setInterval(()=>this.clock(),1000);
	}

	clock(){
		this.setState({
			date: new Date()
		});
	}
  render() {
    return (
      <div>
        <Time date={this.state.date.getHours} />
      </div>
    );
  }
}

const Time = (props) => {
	return(
		<div className = "horaire">
       		<h2>{props.date.getHours()}:{props.date.getMinutes()}:{props.date.getSeconds()}</h2>
      	</div>
	)
}

export default Horloge;
