import React from 'react';
import './Musique.css';
import axios from 'axios';


//URL API
const API_URL = "https://api.music.apple.com/v1/catalog/"

class API_MUSIC{

  constructor(city) {
    // Si la ville n'est pas définit alors la ville par défault est Paris
    if (city === undefined) {
      city = "fr";
    }
    this.city = city;
  }

  fetchMusic() {
      return axios
        .get(`${API_URL}${this.city}/charts`)
        }


   music(){

	const apimusic = new API_MUSIC();

	apimusic
		.fetchMusic()
		.then(function (response) {
		const data = response.data;
		console.log(data);
		const info = data.songs;
		console.log(info);
		})
		.catch(function (error) {
      		// Affiche une erreur
      		console.error(error);
    	});
    }
}

const Musique = ({city}) => {

music();

	return(
		<div>

		</div>
		)

	}

export default Musique;
