import React from 'react';
import ReactDOM from 'react-dom';
import moment from 'moment-timezone';




const Horloge = ({city, continent}) => {

	console.log(continent);
	if (!city || !continent){
		city = "Paris";
		continent = "Europe"
	}

	return(
		<>
		<div> 
			{"Heure à " 
			+ city.replace("_"," ") 
			+" " 
			+moment().tz(continent + "/" + city).format("HH:mm")}  
		</div>
		<div>
			{"Date à " 
			+ city.replace("_"," ") 
			+" " 
			+moment().tz(continent + "/" + city).format("DD/MM/YYYY")}  
		</div>
		</>
		)

	}
export default Horloge;
