import React from 'react';
import './Musique.css';
import axios from 'axios';

//URL API
const API_URL = "https://cors-anywhere.herokuapp.com/https://api.deezer.com/chart/tracks"

class API_MUSIC{

  fetchMusic() {
      return axios
        .get(`${API_URL}`, {
        crossdomain: true
      })
        }
}

function music(){

	const apiMusic = new API_MUSIC();

	const top = apiMusic
		.fetchMusic()
		.then(function(response){

			const data = response.data
			console.log(data);

			for (let i=0; i<5; i++){
				let title = data.tracks.data[i].title;
				let artist = data.tracks.data[i].artist.name;

				console.log("Titre : ", title," / Artiste :", artist);
			}
		})

 		.catch(function (error) {
      		// Affiche une erreur
      		console.error(error);
    	});


	return (
		<div>
			{top.map(title => <div>{title}</div>)}
		</div>
	)

}


const Musique = () => {

	const playlist = music();

		return(
			<div>
				{playlist}
			</div>
		)

}

export default Musique;
