import React from 'react';
import './Musique.css';
import axios from 'axios';

//URL API
const API_URL = "https://cors-anywhere.herokuapp.com/https://api.deezer.com/chart/tracks"

class API_MUSIC{

  fetchMusic() {
      return axios
        .get(`${API_URL}`, {
        crossdomain: true
      })
        }
}

function music(){

const apiMusic = new API_MUSIC();

apiMusic
.fetchMusic()
.then(function(response){
const data = response.data

console.log(data);

let title = data.tracks.data[0].title;
let artist = data.tracks.data[0].artist.name;

console.log("Titre :", title);
console.log("Artiste :", artist);

})


  .catch(function (error) {
      // Affiche une erreur
      console.error(error);
    });

return ("ca marche")
}


const Musique = () => {

const playlist = music();
return(
<div>
{playlist}
</div>
)

}

export default Musique;
