import React from 'react';
import ReactDOM from 'react-dom';
import moment from 'moment-timezone';
import './Horloge.css';

class Clock extends React.Component {
  render() {
    return (
      <div>
        <h1>Hello, world!</h1>
        <h2>It is {this.props.date.toLocaleTimeString()}.</h2>
      </div>
    );
  }
}

function Horloge() {
  ReactDOM.render(
    <Clock date={new Date()} />,
    document.getElementById('root')
  );
