import React from 'react';
import ReactDOM from 'react-dom';
import moment from 'moment-timezone';




const Horloge = ({city, continent}) => {


	if (!city || !continent){
		city = "Paris";
		continent = "Europe"
	}

	return(
		<div> {"Heure à " + city +" " +moment().tz(continent + "/"+ city).format("DD/MM/YYYY HH:mm")}  </div>
		)

	}
export default Horloge;
