import React from 'react';
import ReactDOM from 'react-dom';
import axios from "axios";

//Clé API
const API_KEY = "26622c7dbeec4b9c8c72157f54b12045";
//Url de l'API worldtime
const API_URL = "https://timezone.abstractapi.com/v1/current_time";

class API_TIME {
  constructor(city) {
      // Si la ville n'est pas définit alors la ville par défault est Paris
      if (city === undefined) {
        city = "paris";
      }
      this.city = city;
    }


    fetchTime() {
      return axios
        .get(`${API_URL}?api_key=${API_KEY}&location=${this.city}`, {
          crossdomain: true
        })
  }

}

function time(){

  const apitime = new API_TIME();

  apitime
    .fetchTime()
    .then(function (response) {
      const data = response.data;
    const info = data.datetime;
    console.log(info);
    })
    .catch(function (error) {
        // Affiche une erreur
        console.error(error);
      });
    return(info);
}

const Horloge = () => {

	heure = time();
	return(
		<p> {heure} </p>
		)

	}
export default Horloge;
