import React from 'react';
import './Horloge.css';

class Horloge extends React.Component {

	constructor(props){
		super(props);
		//initalisation de l'état
		this.state = {
			date : new Date(),
			heures : date.getHours(),
			minutes : date.getMinutes(),
			secondes : date.getSecondes()
		};
	}

	//setInterval va exécuter la fonction clock toutes les secondes 
	//pour reproduire une vraie horloge
	componentDidMount() {
		this.heure = setInterval(()=>this.clock(),1000);
	}

	clock(){
		this.setState({
			date: new Date()
		});
	}
  render() {
    return (
      <div>
        <Time date={this.state.date} />
      </div>
    );
  }
}

const Time = (props) => {
	return(
		<div className="horloge">
			<div className = "aiguilles">
				<div className ="haiguille">
       				<h2>{props.date.getHours()}:</h2>
      			</div>
      		</div>
      		<div className = "aiguilles">
				<div className ="maiguille">
       				<h2>{props.date.getMinutes()}:</h2>
      			</div>
      		</div>
      		<div className = "aiguilles">
				<div className ="saiguille">
       				<h2>{props.date.getSeconds()}</h2>
      			</div>
      		</div>
      	</div>
	)
}

export default Horloge;
