import React from 'react';
import moment from 'moment-timezone';
import './Horloge.css';




const Horloge = ({city, continent}) => {

	if (!city || !continent){
		city = "Paris";
		continent = "Europe"
	}

	return(
		<>
		<div className = "lieu"> 
			{"À " 
			+ city.replace("_"," ")}  
		</div>
		<div className="jour">
			{moment().tz(continent + "/" + city).format("DD/MM/YYYY")}
		</div>
		<div className = "heure">
			{"Il est " 
			+" " 
			+moment().tz(continent + "/" + city).format("HH:mm")}  
		</div>
		</>
		)

	}
export default Horloge;
