import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './components/Header';
import Button from './components/Button';
import Horloge from './components/Horloge';
import axios from "axios";

//Clé API
const API_KEY = "26622c7dbeec4b9c8c72157f54b12045";
//Url de l'API worldtime
const API_URL = "https://timezone.abstractapi.com/v1/current_time";

class API_TIME {
  constructor(city) {
      // Si la ville n'est pas définit alors la ville par défault est Paris
      if (city === undefined) {
        city = "paris";
      }
      this.city = city;
    }


    fetchTime() {
      return axios
        .get(`${API_URL}?appid=${API_KEY}&location=${this.city}`, {
          crossdomain: true
        })
  }

}

function time(){

  const apitime = new API_TIME();

  apitime
    .fetchTime()
    .then(function (response) {
      const data = response.data;
    console.info("data");
    const info = data.datetime.info;
    console.log(info);
    })
    .catch(function (error) {
        // Affiche une erreur
        console.error(error);
      });
}

class App extends Component() {
  render(){

  return (
    <div className="container-fluid">
      <header>
        <div className="row">
          <div className="col-md-9 col-sm-9 col-xs-9">
            <Header> Dashboard </Header>
          </div>
          <div className="col-md-3 col-sm-3 col-xs-3">
              <Button> Widgets </Button>
              <Button> Formulaire </Button>
          </div>
        </div>
      </header>
      <main>
        <Horloge />
      </main>
    </div>
  );
  }
}

export default App;
