import './Musique.css';
import axios from 'axios';

//URL API
const API_URL = "https://cors-anywhere.herokuapp.com/https://api.deezer.com/chart/0/tracks"

class API_MUSIC{

  fetchMusic() {
      return axios
        .get(`${API_URL}`, {
        crossdomain: true
      })
        }
}

function music(){

	const apiMusic = new API_MUSIC();

	apiMusic
		.fetchMusic()
		.then(function(response){

		const data = response.data

