import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './components/Header';
import Button from './components/Button';
import axios from "axios";

//Clé API
const API_KEY = "26622c7dbeec4b9c8c72157f54b12045";
//Url de l'API worldtime
const API_URL = "https://timezone.abstractapi.com/v1/current_time";

class API_TIME extends Component {
  constructor(props) {
      super(props);
      this.state = {
          city: "paris"
      };
  }
    callAPI = city => {
    // Call API
    axios
      .get(`${API_URL}?appid=${API_KEY}&location=${this.city}`)
      .then(({ data }) => {
        console.log(data);
        // Recupere uniquement la propriété data
        const { list } = data;

        this.setState({ city });
      })
      .catch(console.error);
  };

  // Lance un appel au lancement du component
  componentDidMount() {
    const { city } = this.props;
    this.callAPI(city);
  }

  // A chaque update relance une api
  componentDidUpdate(nextProps) {
    // Ici on verifie que la mise à jour concerne bien le champs city
    if (nextProps.city !== this.props.city) {
      this.callAPI(nextProps.city);
    }
  }

  render() {
    const { forecast } = this.state;
    const { city } = this.props;
    return (
      <div>
        <h2 className="forecast-header">{city}</h2>
        <div className="forecast-container">

          {/* <Horloge data={city}/>
*/}
        </div>
      </div>
    );
  }
}



function App() {
  return (
    <div className="container-fluid">
      <header>
        <div className="row">
          <div className="col-md-9 col-sm-9 col-xs-9">
            <Header> Dashboard </Header>
          </div>
          <div className="col-md-3 col-sm-3 col-xs-3">
              <Button> Widgets </Button>
              <Button> Formulaire </Button>
          </div>
        </div>
      </header>
      <main onload="">
      </main>
    </div>
  );
}

export default App;
