import React from 'react';
import ReactDOM from 'react-dom';
import moment from 'moment-timezone';
import axios from "axios";

//Clé API
const API_KEY = "26622c7dbeec4b9c8c72157f54b12045";
//Url de l'API worldtime
const API_URL = "https://timezone.abstractapi.com/v1/current_time";

class API_TIME {
  constructor(city) {
      // Si la ville n'est pas définit alors la ville par défault est Paris
      if (city === undefined) {
        city = "paris";
      }
      this.city = city;
    }


    fetchTime() {
      return axios
        .get(`${API_URL}?api_key=${API_KEY}&location=${this.city}`, {
          crossdomain: true
        })
  }

}

function time(){

	let data = null;

  const apitime = new API_TIME();

  console.log(apitime.fetchTime().Promise);

  apitime
    .fetchTime()
    .then(function (response) {
    data = response.data;
  return (<p>{data.datetime}</p>);
    })
    .catch(function (error) {
        // Affiche une erreur
        console.error(error);
    });


}

const Horloge = () => {

	return(
		<div> {console.log(moment().tz("America/Los_Angeles").format())}  </div>
		)

	}
export default Horloge;
