import React from 'react';
import './Musique.css';
import axios from 'axios';

//URL API
const API_URL = "https://cors-anywhere.herokuapp.com/https://api.deezer.com/chart/tracks"

class Musique extends React.Component{

  constructor(props) {
      super(props);
      this.state = {
      	songs : null
      }
   }

  fetchMusic() {
      axios
        .get(`${API_URL}`, {crossdomain: true})
        .then(({data})=>{
        	console.log(data);

        	const songs = [data.tracks.data[0].title, data.tracks.data[1].title, data.tracks.data[2].title, data.tracks.data[3].title,data.tracks.data[4].title];

        	this.setState({songs});
        	    console.log(songs);

        })
        .catch(console.error);
   }



   render(){
   	const { songs } = this.state;

   	return(
   		<div>
   			{songs}
   		</div>)
   }




}

export default Musique;
