import React from 'react';
import ReactDOM from 'react-dom';
import axios from "axios";

//Clé API
const API_KEY = "26622c7dbeec4b9c8c72157f54b12045";
//Url de l'API worldtime
const API_URL = "https://timezone.abstractapi.com/v1/current_time";

fetch(`${API_URL}`).then((response)=> {
	console.log(response)
})

class API_TIME {
	constructor(city) {
		if (city===undefined) {
			city = "paris";
		}
		this.city = city;
	}

	fetchTime () {
		return axios
		.get(`${API_URL}?q=${this.city}`, {
        	crossdomain: true
      	})
	}
}

function Time(city) {
	console.info(city);

	const horaire = new API_TIME(city);

	horaire
		.fetchTime()
		.then(function (response) {
		const data = response.data.list;
		console.log("heure :", data);
		})
}

const Horloge = ({ children }) => {
  return (
      <p>{ children }</p>
  )
}

export default Horloge;
